con:config.script:
def currentDate = new Date().format( 'dd MMMM yyyy' )
def currentDateYYYYMMDD = new Date().format( 'yyyy-MM-dd' )
def ddMMyyyyDateMinus1 = new Date().plus(-1).format( 'dd-MM-yyyy' )
def ddMMyyyyDateMinus2 = new Date().plus(-2).format( 'dd-MM-yyyy' )
def ddMMyyyyDatePlus30 = new Date().plus(30).format( 'dd-MM-yyyy' )
def currentTimeInMillis = new Date().getTime();
def threadIndex = context.ThreadIndex;
def externalId = "" + threadIndex + context.testCase.testSuite.project.getPropertyValue('extIdDelimiter') + currentTimeInMillis;

context.testCase.setPropertyValue('ddMMyyyyDateMinus1', ddMMyyyyDateMinus1)
context.testCase.setPropertyValue('ddMMyyyyDateMinus2', ddMMyyyyDateMinus2)
context.testCase.setPropertyValue('currentDate', currentDate)
context.testCase.setPropertyValue('ddMMyyyyDatePlus30', ddMMyyyyDatePlus30)
context.testCase.setPropertyValue('externalId', externalId)


context.testCase.testSuite.project.setPropertyValue('externalId', externalId)

con:configuration.scriptText:
import groovy.json.JsonSlurper 
def response = messageExchange.response.responseContent

def slurper = new JsonSlurper()
def json = slurper.parseText response 

messageExchange.modelItem.testCase.setPropertyValue( "productId" , "" + json."savingsProductId")
messageExchange.modelItem.testCase.setPropertyValue( "interestRate" , "" + json."nominalAnnualInterestRateOverdraft")
messageExchange.modelItem.testCase.setPropertyValue( "accountNumber" , "" + json."accountNo")
messageExchange.modelItem.testCase.setPropertyValue( "overdraftLimit" , "" + json."overdraftLimit")
messageExchange.modelItem.testCase.setPropertyValue( "status" , "" + json."status"."id")
messageExchange.modelItem.testCase.setPropertyValue( "subStatus" , "" + json."subStatus"."id")

//optional fields
if (json."summary" != null) {
messageExchange.modelItem.testCase.setPropertyValue( "balance" , "" + json."summary"."accountBalance")
}

if (json."transactions"!=null) {
messageExchange.modelItem.testCase.setPropertyValue( "transaction1TypeId" , "" + json."transactions".last()."transactionType"."id")
messageExchange.modelItem.testCase.setPropertyValue( "transaction1Date" , "" + json."transactions".last()."date")
messageExchange.modelItem.testCase.setPropertyValue( "transaction1Amount" , "" + json."transactions".last()."amount")
}
