con:config.script:
def currentDate = new Date().format( 'dd MMMM yyyy' )
def currentDateYYYYMMDD = new Date().format( 'yyyy-MM-dd' )
def currentDateMinusOne = new Date().plus(-1).format( 'dd MMMM yyyy' )
def currentDateMinus30 = new Date().plus(-30).format( 'dd MMMM yyyy' )
def currentDateMinus50 = new Date().plus(-50).format( 'dd MMMM yyyy' )
def currentDateMinus50YYYYMMDD = new Date().plus(-50).format( 'yyyy-MM-dd' )
def currentDateMinus60 = new Date().plus(-60).format( 'dd MMMM yyyy' )
def currentDateMinus10 = new Date().plus(-10).format( 'dd MMMM yyyy' )
def currentDateMinus10YYYYMMDD = new Date().plus(-10).format( 'yyyy-MM-dd' )
def currentDatePlus30 = new Date().plus(30).format( 'dd MMMM yyyy' )
def currentTimeInMillis = new Date().getTime();
def threadIndex = context.ThreadIndex;
def externalId = "" + threadIndex + context.testCase.testSuite.project.getPropertyValue('extIdDelimiter') + currentTimeInMillis;

context.testCase.setPropertyValue('queryAccountInfoIndex', "0")
context.testCase.setPropertyValue('withdrawIndex', "0")
context.testCase.setPropertyValue('depositIndex', "0")
context.testCase.setPropertyValue('postInterestIndex', "0")


context.testCase.setPropertyValue('currentDateYYYYMMDD', currentDateYYYYMMDD)
context.testCase.setPropertyValue('dateMinus10YYYYMMDD', currentDateMinus10YYYYMMDD)
context.testCase.setPropertyValue('currentDate', currentDate)
context.testCase.setPropertyValue('currentDateMinus30', currentDateMinus30)
context.testCase.setPropertyValue('currentDateMinus50', currentDateMinus50)
context.testCase.setPropertyValue('dateMinus50YYYYMMDD', currentDateMinus50YYYYMMDD)
context.testCase.setPropertyValue('currentDateMinus60', currentDateMinus60)
context.testCase.setPropertyValue('currentDateMinus10', currentDateMinus10)
context.testCase.setPropertyValue('currentDateMinusOne', currentDateMinusOne)
context.testCase.setPropertyValue('currentDatePlus30', currentDatePlus30)
context.testCase.setPropertyValue('externalId', externalId)


context.testCase.testSuite.project.setPropertyValue('externalId', externalId)
context.testCase.testSuite.project.setPropertyValue('currentDateMinus50YYYYMMDD', currentDateMinus50YYYYMMDD)
context.testCase.testSuite.project.setPropertyValue('dateMinus10YYYYMMDD', currentDateMinus10YYYYMMDD)

con:configuration.scriptText:
def withdrawIndex = messageExchange.modelItem.testCase.getPropertyValue( "withdrawIndex" )
if (withdrawIndex == null) {
	withdrawIndex = "0"
} 

withdrawIndex = Integer.valueOf(withdrawIndex) + 1
messageExchange.modelItem.testCase.setPropertyValue( "withdrawIndex", "" + withdrawIndex)

con:config.script:
def withdrawNumCalls = context.testCase.getPropertyValue( "withdrawNumCalls" )
def withdrawIndex = context.testCase.getPropertyValue( "withdrawIndex" )

def loopDelay = Integer.valueOf(context.testCase.getPropertyValue( "loopDelay" ))

if (Integer.valueOf(withdrawIndex) < Integer.valueOf(withdrawNumCalls)) {
	sleep(loopDelay)
	log.info "LOOP - Withdraw. withdrawIndex: " + withdrawIndex
	testRunner.gotoStepByName("LOOP - Withdraw")
} else {
	log.info "continue after withdrawIndex loop. withdrawIndex: " + withdrawIndex
}

