con:config.script:
def currentDate = new Date().format( 'dd MMMM yyyy' )
def currentDateYYYYMMDD = new Date().format( 'yyyy-MM-dd' )
def currentDateMinusOne = new Date().plus(-1).format( 'dd MMMM yyyy' )
def currentDateMinus120 = new Date().plus(-120).format( 'dd MMMM yyyy' )
def externalIdIndex = new Integer(context.testCase.testSuite.project.getPropertyValue('externalIdIndex'))
def externalIdPrefix = context.testCase.testSuite.project.getPropertyValue('externalIdPrefix')

context.testCase.testSuite.project.setPropertyValue('currentDate', currentDate)
context.testCase.testSuite.project.setPropertyValue('currentDateYYYYMMDD', currentDateYYYYMMDD)
context.testCase.testSuite.project.setPropertyValue('currentDateMinusOne', currentDateMinusOne)
context.testCase.testSuite.project.setPropertyValue('externalIdIndex', "" + (externalIdIndex + 1))
context.testCase.testSuite.project.setPropertyValue('externalId', "" + externalIdPrefix + (externalIdIndex + 1))
