def currentDate = new Date().format( 'dd MMMM yyyy' )
def currentDateYYYYMMDD = new Date().format( 'yyyy-MM-dd' )
def currentDateMinusOne = new Date().plus(-1).format( 'dd MMMM yyyy' )
def currentDateMinus30 = new Date().plus(-30).format( 'dd MMMM yyyy' )
def currentDateMinus10 = new Date().plus(-10).format( 'dd MMMM yyyy' )
def currentDateMinus10YYYYMMDD = new Date().plus(-10).format( 'yyyy-MM-dd' )
def currentDatePlus30 = new Date().plus(30).format( 'dd MMMM yyyy' )
def currentTimeInMillis = new Date().getTime();
def externalId = context.testCase.testSuite.project.getPropertyValue('ticketNo') + currentTimeInMillis;

context.testCase.testSuite.project.setPropertyValue('currentDateYYYYMMDD', currentDateYYYYMMDD)
context.testCase.testSuite.project.setPropertyValue('dateMinus10YYYYMMDD', currentDateMinus10YYYYMMDD)
context.testCase.testSuite.project.setPropertyValue('currentDate', currentDate)
context.testCase.testSuite.project.setPropertyValue('currentDateMinus30', currentDateMinus30)
context.testCase.testSuite.project.setPropertyValue('currentDateMinus10', currentDateMinus10)
context.testCase.testSuite.project.setPropertyValue('currentDateMinusOne', currentDateMinusOne)
context.testCase.testSuite.project.setPropertyValue('currentDatePlus30', currentDatePlus30)
context.testCase.testSuite.project.setPropertyValue('externalId', externalId)

